mapper.select:
SELECT

FROM USER_BASE AS U
INNER JOIN USER_DETAIL AS UD
ON U.USER_ID = UD.USER_ID
WHERE U.USER_ID = #{userId}


AND UD.UPDATE_TIME > #{lastUpdateTime}

SELECT

FROM USER_BASE AS U
INNER JOIN USER_DETAIL AS UD
ON U.USER_ID = UD.USER_ID
WHERE U.USER_NAME = #{userName}
AND U.USER_PASSWORD = #{password}

mapper.update:
UPDATE USER_BASE SET
UUID = #{baseEntity.uuid},
DEVICE_ID = #{baseEntity.deviceId}, SEX = #{baseEntity.sex}, PLATFORM_INFO = #{baseEntity.platformInfo}, UPDATE_TIME = now() WHERE USER_ID = #{baseEntity.userId}

mapper.select:
SELECT

FROM FRIENDSHIP AS UF
WHERE UF.USER_ID = #{userId}
AND UF.FRIEND_ID = #{friendId}

mapper.update:
UPDATE FRIENDSHIP
SET
    LAST_WALK_TIME = now(),
    UPDATE_TIME = now()
WHERE USER_ID = #{userId}
AND FRIEND_ID = #{friendId}

mapper.select:
SELECT
FROM FRIENDSHIP AS UF WHERE (UF.USER_ID = #{userId} OR UF.FRIEND_ID = #{userId})
AND UPDATE_TIME > #{lastUpdateTime}

SELECT DISTINCT
FROM FRIENDSHIP AS UF INNER JOIN USER_BASE AS U ON UF.USER_ID = U.USER_ID OR UF.FRIEND_ID = U.USER_ID INNER JOIN USER_DETAIL AS UD ON U.USER_ID = UD.USER_ID WHERE (UF.USER_ID = #{userId} OR UF.FRIEND_ID = #{userId})
AND UF.UPDATE_TIME > #{lastUpdateTime}

SELECT

FROM USER_PROP AS UP
WHERE UP.USER_ID = #{userId}


AND UP.UPDATE_TIME > #{lastUpdateTime}
